def handle_length (tableau)
    puts tableau.length()
    puts "Le tableau contient #{tableau.length()} éléments."
end

def handle_min_length (tableau)
    min_handle = tableau.min{|a,b| a.length <=> b.length}
    puts "Le handle le plus court fait #{(min_handle.length)-1} caractères."
end

def handle_5_caracter (tableau)
    i = 0
    tableau.each do |n|
        if (n.length()-1) == 5
            i += 1
        end
    end
    puts "Il y a #{i} handle contenant 5 caractères." 
end

def handle_1st_letter_uppercase (tableau)
    i = 0
    vari = []
    tableau.each do |n|
        h = 0
        n.length.times do |j|
            if n[j].match?(/[[:alpha:]]/) == true && h == 0
                if n[j] == n[j].upcase
                    i += 1 
                    vari << n
                end
            h += 1
            end
        end
    end
    puts vari
    puts "Il y a #{i} handle qui ont leur première lettre en majuscule." 
end

def handle_sort_by_alphabetic (tableau)
    journalist_sorted = tableau.map {|n| n.downcase}
    journalist_sorted = journalist_sorted.sort
    puts "Voici la liste des handles classée par ordre alphabétique: "
    puts journalist_sorted
end

def handle_sort_by_size (tableau)
    sort_by_size = tableau.sort_by {|x| x.length}
    puts "Voici la liste des handles classée par ordre de taille croissant:"
    puts  sort_by_size
end

def handle_epenser1 (tableau)
    epenser_index = tableau.index{|s| s == ("@epenser")}
    puts "Voici l'index de epenser dans le tableau: #{epenser_index}"
end

def handle_grouped_by_size(tableau)
    compteur = []
    (tableau.length).times do |i|
        i += 1
        h = 0
        tableau.each do |n|
            if n.length == i+1
                h += 1
            end      
        end 
        compteur << (h)
    end
    compteur.each_with_index{|n,index| if n != 0 
                            puts "Il y a #{n} handle(s) contenant #{index+1} caractères!" 
                            end}
end

def menu(tableau)
    b = "oui"
    while b == "oui"
    a = tableau
    
    puts "Ici on balance de l'info comme ça! Tiens dit moi ce que tu veux et je le met direct dans ta bouche <3"
    puts "Tape 1 pour savoir combien le tableau contient d'élément."
    puts "Tape 2 pour savoir combien de caractère fait le pseudo le plus petit du tableau."
    puts "Tape 3 pour savoir combien de pseudo contiennent 5 caractères."
    puts "Tape 4 pour savoir combien de handle ont leur première lettre en majuscule."
    puts "Tape 5 pour classer le tableau par ordre alphabétique."
    puts "Tape 6 pour classer le tableau par ordre croissant de taille de pseudo."
    puts "Tape 7 pour connaître l'index de @epenser dans le tableau."
    puts "Tape 8 si tu veux que je répartisse les pseudo par taille et que je te dise combien de pseudo il y a dans chaque groupe."
    print ">"

    input = gets.chomp.to_i

    case input
    when 1
        handle_length (a)
    when 2 
        handle_min_length (a)
    when 3 
        handle_5_caracter (a)
    when 4 
        handle_1st_letter_uppercase (a)
    when 5 
        handle_sort_by_alphabetic (a)
    when 6 
        handle_sort_by_size (a)
    when 7 
        handle_epenser1 (a)
    when 8
        handle_grouped_by_size (a)
    end
    
    puts "Si tu veux savoir d'autres trucs dit 'oui' sinon dit 'non'"
    print">"
    b = gets.chomp
    end
end

menu(journalist)